import "./App.css";
import { useFormik } from "formik";

function App() {
  const formik = useFormik({
    initialValues: {
      userName: "",
      password: "",
    },
    onSubmit: (values) => {
      console.log("form: ", values);
    },
    validate: (values) => {
      let errors = {};
      if (!values.userName) errors.userName = "Field required";
      alert();
    },
  });
  return (
    <div>
      <form>
        <div>Username</div>
        <input name="userName" id="emailField" type="text" />
        <div>Password</div>
        <input name="password" id="pswField" type="text" />
        <button id="submitBtn" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
}

export default App;
